mapper.insert:
INSERT INTO tbl_member (

) VALUES (
    #{member_idx}
    , #{userId}
    , #{email}
    , IFNULL(#{password}, password)
    , #{nickname}
    , #{name}
    , #{delete_yn}
    , NOW()
    , NULL
    , #{role}
)
ON DUPLICATE KEY UPDATE
    password = IFNULL(#{password}, password)
    , nickname = #{nickname}
    , email = #{email}

mapper.select:
SELECT 
FROM tbl_member
WHERE delete_yn='N' AND member_idx=#{member_idx}

SELECT 
FROM tbl_member
WHERE delete_yn='N' AND userId=#{userId}

SELECT userId
FROM tbl_member
WHERE name=#{name} AND email=#{email}

SELECT member_idx
FROM tbl_member
WHERE userId=#{userId} AND name=#{name}

mapper.update:
UPDATE tbl_member
SET password=#{password}
WHERE member_idx=#{member_idx}
